$(document).ready(function () {
    $('.slider__inner').slick({
        speed: 1200,
        adaptiveHeight: true,
        autoplay: true,
        autoplaySpeed: 2000,
        prevArrow: '<button type="button" class="slick-prev"><img class="slick__img" src="../imgs/slider/arrow_left.png"></button>',
        nextArrow: '<button type="button" class="slick-next"><img class="slick__img" src="../imgs/slider/arrow_right.png"></button>',
        responsive: [
            {
                breakpoint: 768,
                settings: {
                    dots: true,
                    arrows: false,
                }
            }
        ]

    });

    $('ul.catalog__tabs').on('click', 'li:not(catalog__tab_active)', function () {
        $(this)
            .addClass('catalog__tab_active').siblings().removeClass('catalog__tab_active')
            .closest('div.container').find('div.catalog-content').removeClass('catalog-content_active').eq($(this).index()).addClass('catalog-content_active');
    });

    function toggleSlide(item) {
        $(item).each(function (i) {
            $(this).on('click', function (e) {
                e.preventDefault();
                $('.catalog-content__external').eq(i).toggleClass('catalog-content__external_active');
                $('.catalog-content__enternal').eq(i).toggleClass('catalog-content__enternal_active')
            })
        })

    };

    toggleSlide('.catalog-content__more');
    toggleSlide('.catalog-content__back');
});